DOMTestCase('documentgetdoctype') do

  ###
  # Constructor.
  # @param factory document factory, may not be null
  # @throws org.w3c.domts.DOMTestIncompatibleException Thrown if test is not compatible with parser configuration
  ##
  def setup
    ##
    ##   check if loaded documents are supported for content type
    ##
    contentType = getContentType()
    preload(contentType, "staff", false)
  end

  ###
  # Runs the test case.
  # @throws Throwable Any uncaught exception causes test to fail
  #
  def test_documentgetdoctype
    doc = nil
    docType = nil
    docTypeName = nil
    nodeValue = nil
    doc = load_document("staff", false)
      docType = doc.doctype()
      assert_not_nil(docType, "docTypeNotNull")
      docTypeName = docType.name()
      
      if (("image/svg+xml" == getContentType()))
        assert_equal("svg", docTypeName, "doctypeNameSVG")
            
          else
            assert_equal("staff", docTypeName, "doctypeName")
            
         end
       nodeValue = docType.nodeValue()
      assert_nil(nodeValue, "initiallyNull")
      
  end

