DOMTestCase('elementwrongdocumenterr') do

  ###
  # Constructor.
  # @param factory document factory, may not be null
  # @throws org.w3c.domts.DOMTestIncompatibleException Thrown if test is not compatible with parser configuration
  ##
  def setup
    ##
    ##   check if loaded documents are supported for content type
    ##
    contentType = getContentType()
    preload(contentType, "staff", true)
    preload(contentType, "staff", false)
  end

  ###
  # Runs the test case.
  # @throws Throwable Any uncaught exception causes test to fail
  #
  def test_elementwrongdocumenterr
    doc1 = nil
    doc2 = nil
    newAttribute = nil
    addressElementList = nil
    testAddress = nil
    attrAddress = nil
    doc1 = load_document("staff", true)
      doc2 = load_document("staff", false)
      newAttribute = doc2.createAttribute("newAttribute")
      addressElementList = doc1.getElementsByTagName("address")
      testAddress = addressElementList.item(4)
      
    begin
      success = false;
      begin
        attrAddress = testAddress.setAttributeNode(newAttribute)
      rescue Nokogiri::XML::DOMException => ex
        success = (ex.code == Nokogiri::XML::DOMException::WRONG_DOCUMENT_ERR)
      end 
      assert(success, "throw_WRONG_DOCUMENT_ERR")
    end

  end

