    contentType = getContentType()
    preload(contentType, "staff", false)
  end

  ###
  # Runs the test case.
  # @throws Throwable Any uncaught exception causes test to fail
  #
  def test_nodelistreturnlastitem
    doc = nil
    elementList = nil
    employeeNode = nil
    employeeList = nil
    child = nil
    childName = nil
    length = nil
    doc = load_document("staff", false)
      elementList = doc.getElementsByTagName("employee")
      employeeNode = elementList.item(2)
      employeeList = employeeNode.childNodes()
      length = employeeList.length()
      
      if (equals(6, length))
        child = employeeList.item(5)
      childName = child.nodeName()
      assert_equal("address", childName, "nodeName1")
            
          else
            child = employeeList.item(12)
      childName = child.nodeName()
      assert_equal("#text", childName, "nodeName2")
            
         end
       
