  #
  def test_prefix05
    doc = nil
    elementList = nil
    addrNode = nil
    addrAttr = nil
    doc = load_document("staffNS", true)
      elementList = doc.getElementsByTagName("emp:address")
      addrNode = elementList.item(0)
      assert_not_nil(addrNode, "empAddrNotNull")
      addrAttr = addrNode.getAttributeNode("emp:domestic")
      
    begin
      success = false;
      begin
        addrAttr.prefix = "xmlns"
      rescue Nokogiri::XML::DOMException => ex
        success = (ex.code == Nokogiri::XML::DOMException::NAMESPACE_ERR)
      end 
      assert(success, "throw_NAMESPACE_ERR")
    end

