  def test_HTMLTableSectionElement31
    nodeList = nil
    testNode = nil
    rowsnodeList = nil
    vrows = nil
    doc = nil
    doc = load_document("tablesection", true)
      nodeList = doc.getElementsByTagName("tbody")
      assertSize("Asize", 2, nodeList)
